/* eslint-disable react/jsx-no-undef */
import React, { useEffect, useState } from "react";
import { Form, Button } from "react-bootstrap";

//import DragScaleBar from 'react-drag-scale-bar'
// import { Container } from './styles';
type TypeSub = {
  [page: string]: {
    x: number;
    y: number;
    index: number;
    width: number;
    size: number;
    text: string;
    style: React.CSSProperties;
  }[];
};
const EditSub: React.FC<{
  sub: TypeSub;
  setSub: (a: TypeSub) => void;
  page?: string;
}> = ({ sub, setSub, page }) => {
  const [index, setIndex] = useState(1);
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);
  const [width, setWidth] = useState(0);
  const [size, setSize] = useState(0);

  useEffect(() => {
    if (page) {
      const text = sub[page]?.find((a) => a.index === index);
      setX(text?.x || x);
      setY(text?.y || y);
      setWidth(text?.width || width);
      setSize(text?.size || size);
    }
  }, [index]);

  return (
    <Form>
      <Form.Group controlId="formIndex">
        <Form.Label>Index</Form.Label>
        <Form.Control
          value={index}
          onChange={(e) => setIndex(e.target.value)}
          type="number"
          min="0"
          step="1"
          placeholder="index"
        />
      </Form.Group>

      <Form.Group controlId="formX">
        <Form.Label>X axis</Form.Label>
        <Form.Control
          value={x}
          onChange={(e) => setX(e.target.value)}
          type="number"
          min="0"
          max="100"
          step="0.01"
          placeholder="X"
        />
      </Form.Group>

      <Form.Group controlId="formY">
        <Form.Label>Y axis</Form.Label>
        <Form.Control
          value={y}
          onChange={(e) => setY(e.target.value)}
          type="number"
          min="0"
          max="100"
          step="0.01"
          placeholder="Y"
        />
      </Form.Group>
      <Form.Group controlId="formY">
        <Form.Label>Size</Form.Label>
        <Form.Control
          value={size}
          onChange={(e) => setSize(e.target.value)}
          type="number"
          min="0"
          max="100"
          step="0.01"
          placeholder="size"
        />
      </Form.Group>
      <Form.Group controlId="formY">
        <Form.Label>Width</Form.Label>
        <Form.Control
          value={width}
          onChange={(e) => setWidth(e.target.value)}
          type="number"
          min="0"
          max="100"
          step="0.01"
          placeholder="width"
        />
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
};

export default EditSub;
