/* eslint-disable react/jsx-no-undef */
import React, { useEffect, useState } from "react";
import { Form, Button, DropdownButton, Dropdown, Row } from "react-bootstrap";
import {
  parseObjectToPSRT,
  parsePSRTFileToObject,
  parsePSRTToObject,
} from "../service/subtitle";

//import DragScaleBar from 'react-drag-scale-bar'
// import { Container } from './styles';
type TypeUseState<T> = (a: T | ((a: T) => T)) => void;
type TypeSub = {
  [page: string]: {
    x: number;
    y: number;
    index: number;
    width: number;
    size: number;
    text: string;
    style: React.CSSProperties;
  }[];
};
const EditSub: React.FC<{
  sub: TypeSub;
  setSub: TypeUseState<TypeSub>;
  page?: string;
  setPage: TypeUseState<string>;
  setImage: TypeUseState<string>;
  setImageStyle: TypeUseState<React.CSSProperties | string>;
}> = ({
  sub,
  setSub,
  page: defaultPage,
  setPage: setDefaultPage,
  setImage,
  setImageStyle,
}) => {
  const [page, setPage] = useState(defaultPage || "page1");
  const [isNewPage, setIsNewPage] = useState(false);
  const [index, setIndex] = useState<string | number>(1);
  const [x, setX] = useState<string | number>(0);
  const [y, setY] = useState<string | number>(0);
  const [width, setWidth] = useState<string | number>(0);
  const [size, setSize] = useState<string | number>(0);
  const [subText, setSubText] = useState("");
  const [show, setShow] = useState(true);
  const [style, setStyle] = useState<{ [cssAttr: string]: string }>({});
  const [keyStyle, setKeyStyle] = useState<string>("");
  const [valueStyle, setValueStyle] = useState<string>("");
  const [lastIndex, setLastIndex] = useState(
    sub[`${page}`]?.reduce((p, c) => (c.index > p ? c.index : p), 0) + 1
  );

  const [clearSub, setClearSub] = useState(sub);

  const handleChangeSub = () => {
    if (!isNewPage) {
      setSub((data) => ({
        ...data,
        [page]: [
          ...sub[page]?.map((txt) => {
            if (txt.index === parseInt(index)) {
              return {
                ...txt,
                x: parseFloat(x),
                y: parseFloat(y),
                width: parseFloat(width),
                size: parseFloat(size),
                text: subText,
                style: {
                  ...style,
                  backgroundColor: "#0f0",
                },
              };
            } else {
              if (txt.style.backgroundColor === "#0f0") {
                delete txt.style.backgroundColor;
              }
              return txt;
            }
          }),
        ],
      }));

      setClearSub((data) => ({
        ...data,
        [page]: [
          ...sub[page]?.map((txt) => {
            if (txt.index === parseInt(index)) {
              return {
                ...txt,
                x: parseFloat(x),
                y: parseFloat(y),
                width: parseFloat(width),
                size: parseFloat(size),
                text: subText,
                style: {
                  ...style,
                },
              };
            } else {
              return txt;
            }
          }),
        ],
      }));
    }
  };

  useEffect(() => {
    handleChangeSub();
  }, [x, y, width, size, subText, style]);

  const updateSubInfos = (newSub?: TypeSub) => {
    const _sub = newSub || sub;
    if (page) {
      const text = _sub[page]?.find((a) => a.index === parseInt(index));
      setX(text?.x || x);
      setY(text?.y || y);
      setWidth(text?.width || width);
      setSize(text?.size || size);
      setSubText(text?.text || "");
      setStyle({ ...text?.style } || { ...style });
    }
  };
  useEffect(() => {
    updateSubInfos();
  }, [index, page]);

  useEffect(() => {
    setIsNewPage(!Object.keys(sub).includes(page));
  }, [page]);

  const addNewText = () => {
    setSub((data) => ({
      ...data,
      [page]: [
        ...data[page],
        {
          index: parseInt(String(lastIndex)),
          x: parseFloat(String(x)),
          y: parseFloat(String(y)),
          width: parseFloat(String(width)),
          size: parseFloat(String(size)),
          text: subText,
          style: {
            ...style,
            backgroundColor: "#0f0",
          },
        },
      ],
    }));

    setIndex(lastIndex);
    setLastIndex((old) => old + 1);
  };

  const addNewPage = () => {
    if (isNewPage) {
      setSub({
        ...clearSub,
        [page]: [],
      });
      console.log(page);

      updateSubInfos(sub);

      setIndex(0);
      setLastIndex(1);
      setDefaultPage(page);
      updateSubInfos();
    }
    console.log(sub);
  };

  function download(data, filename, type) {
    var file = new Blob([data], { type: type });
    if (window.navigator.msSaveOrOpenBlob)
      // IE10+
      window.navigator.msSaveOrOpenBlob(file, filename);
    else {
      // Others
      var a = document.createElement("a"),
        url = URL.createObjectURL(file);
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      setTimeout(function () {
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
      }, 0);
    }
  }

  const formatSub = (sub: TypeSub): TypeSub => {
    if (page) {
      sub[page].map((txt) => {
        if (txt.style.backgroundColor === "#0f0") {
          delete txt.style.backgroundColor;
        }
        return txt;
      });
    }
    return sub;
  };

  const getLocalImage = (callback: (fr: FileReader) => any) => (evt) => {
    let tgt = evt.target;
    let files = tgt.files;

    // FileReader support
    if (FileReader && files && files.length) {
      let fr = new FileReader();
      fr.onload = () => callback(fr);
      fr.readAsDataURL(files[0]);
    }
  };

  const getLocalSubtitle = (callback: (fr: FileReader) => any) => (evt) => {
    let tgt = evt.target;
    let files = tgt.files;

    // FileReader support
    if (FileReader && files && files.length) {
      let fr = new FileReader();
      fr.onload = () => callback(fr);
      fr.readAsText(files[0]);
    }
  };

  const updateSubtitleFile = (fr: FileReader) => {
    const newSub = parsePSRTToObject(String(fr.result));
    setSub(newSub);
    updateSubInfos(newSub);
  };
  const showImage = (fr: FileReader) => setImage(fr.result);

  const handleAddStyle = () => {
    setStyle((old) => ({ ...old, [keyStyle]: valueStyle }));
    setValueStyle("");
    setKeyStyle("");
  };

  const handleRemoveStyle = (key: string) => {
    setValueStyle(style[key]);
    setKeyStyle(key);
    const newStyles = { ...style };

    delete newStyles[key];

    setStyle(newStyles);
  };

  return (
    <Form
      style={{
        position: "fixed",
        width: "500px",
        left: 0,
        overflowY: "auto",
        overflowX: "hidden",
        zIndex: 50,
      }}
    >
      <div className={`form-check form-switch mr-0`}>
        <input
          className="form-check-input"
          type="checkbox"
          onClick={() => setShow((a) => !a)}
          id="flexSwitchCheckChecked"
          checked={show}
        />
        <label className="form-check-label" htmlFor="flexSwitchCheckChecked">
          Settings
        </label>
      </div>
      {show && (
        <div>
          <Row>
            <Form.Group className=" col-6" controlId="subtitle-file">
              <Form.Label className="btn btn-primary">Load Image</Form.Label>
              <Form.Control
                onChange={getLocalImage(showImage)}
                type="file"
                style={{ visibility: "hidden" }}
                placeholder="Image"
              />
            </Form.Group>

            <Form.Group className=" col-6" controlId="image-file">
              <Form.Label className="btn btn-primary">Load SubTitle</Form.Label>
              <Form.Control
                onChange={getLocalSubtitle(updateSubtitleFile)}
                type="file"
                style={{ visibility: "hidden" }}
                placeholder="Image"
              />
            </Form.Group>
          </Row>

          <Row style={{ alignItems: "flex-end" }}>
            <Form.Group className=" col-3 mb-0" controlId="formPage">
              <Form.Label>Page</Form.Label>
              <Form.Control
                value={page}
                onChange={(e) => setPage(e.target.value)}
                placeholder="Page"
              />
            </Form.Group>

            <Button
              className=" col-2"
              style={{ height: "100%" }}
              onClick={addNewPage}
              disabled={!isNewPage}
              variant="primary"
            >
              Add page
            </Button>

            <Form.Group className=" col-3 mb-0" controlId="formIndex">
              <Form.Label>Index</Form.Label>
              <Form.Control
                value={index}
                onChange={(e) => setIndex(e.target.value)}
                type="number"
                min="0"
                max={sub[String(page)]?.length}
                step="1"
                placeholder="index"
              />
            </Form.Group>

            <Button
              className=" col-2"
              style={{ height: "100%" }}
              onClick={addNewText}
              variant="primary"
            >
              Add Text
            </Button>
          </Row>
          <Row>
            <Form.Group className=" col-3" controlId="formX">
              <Form.Label>X axis</Form.Label>
              <Form.Control
                value={x}
                onChange={(e) => setX(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="X"
              />
            </Form.Group>

            <Form.Group className=" col-3" controlId="formY">
              <Form.Label>Y axis</Form.Label>
              <Form.Control
                value={y}
                onChange={(e) => setY(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="Y"
              />
            </Form.Group>

            <Form.Group className=" col-3" controlId="formY">
              <Form.Label>Size</Form.Label>
              <Form.Control
                value={size}
                onChange={(e) => setSize(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="size"
              />
            </Form.Group>
            <Form.Group className=" col-3" controlId="formWidth">
              <Form.Label>Width</Form.Label>
              <Form.Control
                value={width}
                onChange={(e) => setWidth(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="width"
              />
            </Form.Group>
          </Row>
          <Form.Group controlId="formText">
            <Form.Label>Text</Form.Label>
            <Form.Control
              value={subText}
              onChange={(e) => setSubText(e.target.value)}
              type="text"
              placeholder="width"
            />
          </Form.Group>
          <Row>
            <Row>
              <Form.Group className=" col-2" controlId="formKeyStyle">
                <Form.Control
                  value={keyStyle}
                  onChange={(e) => setKeyStyle(e.target.value)}
                  type="text"
                  placeholder="key"
                />
              </Form.Group>
              <Form.Group className=" col-2" controlId="formValueStyle">
                <Form.Control
                  value={valueStyle}
                  onChange={(e) => setValueStyle(e.target.value)}
                  type="text"
                  placeholder="value"
                />
              </Form.Group>
            </Row>
            <Button
              className="mt-2 mb-3 col-sm btn-secondary"
              onClick={handleAddStyle}
            >
              Add Note
            </Button>
            {Object.keys(style).length > 0 && (
              <DropdownButton
                className=" m-2 col-sm"
                variant="secondary"
                id="dropdown-basic-button"
                title="Notes"
              >
                {Object.keys(style).length > 0 &&
                  Object.keys(style)?.map((styleProp) => (
                    <Dropdown.Item
                      onClick={() => handleRemoveStyle(styleProp)}
                      key={styleProp}
                    >
                      {`${styleProp.replaceAll("_", " ")} : ${
                        style[styleProp]
                      }`}
                    </Dropdown.Item>
                  ))}
              </DropdownButton>
            )}
          </Row>
          <div>
            <Button
              onClick={() =>
                download(parseObjectToPSRT(formatSub(sub)), "sub.psrt", "jpg")
              }
              variant="primary"
            >
              Dowload Subtitle (.psrt)
            </Button>
            <Button
              onClick={() =>
                download(JSON.stringify(formatSub(sub)), "sub.json", "jpg")
              }
              variant="primary"
            >
              Dowload Subtitle (.json)
            </Button>
          </div>
        </div>
      )}
    </Form>
  );
};

export default EditSub;
