            <Form.Group className=" col-3 mb-0" controlId="formIndex">
              <Form.Label>Index</Form.Label>
              <Form.Control
                value={index}
                onChange={(e) => setIndex(e.target.value)}
                type="number"
                min="0"
                max={sub[String(page)]?.length}
                step="1"
                placeholder="index"
              />
            </Form.Group>

            <Button className=" col-2" onClick={addNewText} variant="primary">
              Add Text
            </Button>
          </Row>
          <Row>
            <Form.Group className=" col-3" controlId="formX">
              <Form.Label>X axis</Form.Label>
              <Form.Control
                value={x}
                onChange={(e) => setX(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="X"
              />
            </Form.Group>

            <Form.Group className=" col-3" controlId="formY">
              <Form.Label>Y axis</Form.Label>
              <Form.Control
                value={y}
                onChange={(e) => setY(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="Y"
              />
            </Form.Group>

            <Form.Group className=" col-3" controlId="formY">
              <Form.Label>Size</Form.Label>
              <Form.Control
                value={size}
                onChange={(e) => setSize(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="size"
              />
            </Form.Group>
            <Form.Group className=" col-3" controlId="formWidth">
              <Form.Label>Width</Form.Label>
              <Form.Control
                value={width}
                onChange={(e) => setWidth(e.target.value)}
                type="number"
                min="0"
                max="100"
                step="0.1"
                placeholder="width"
              />
            </Form.Group>
          </Row>
          <Row>
            <Form.Group className=" col-8" controlId="formText">
              <Form.Label>Text</Form.Label>
              <Form.Control
                value={subText}
                onChange={(e) => setSubText(e.target.value)}
                type="text"
                placeholder="width"
              />
            </Form.Group>
            {Object.keys(style).length > 0 && (
              <DropdownButton
                className="col-4"
                variant="secondary"
                id="dropdown-basic-button"
                title="Styles"
              >
                {Object.keys(style).length > 0 &&
                  Object.keys(style)?.map((styleProp) => (
                    <Dropdown.Item
                      onClick={() => handleRemoveStyle(styleProp)}
                      key={styleProp}
                    >
                      {`${styleProp.replaceAll("_", " ")} : ${
                        style[styleProp]
                      }`}
                    </Dropdown.Item>
                  ))}
              </DropdownButton>
            )}
          </Row>
          <Row>
            <Form.Group className=" col-4" controlId="formKeyStyle">
              <Form.Control
                value={keyStyle}
                onChange={(e) => setKeyStyle(e.target.value)}
                type="text"
                placeholder="key"
              />
            </Form.Group>
            <Form.Group className=" col-4" controlId="formValueStyle">
              <Form.Control
                value={valueStyle}
                onChange={(e) => setValueStyle(e.target.value)}
                type="text"
                placeholder="value"
              />
            </Form.Group>

            <Button
              style={{ height: "100%" }}
              className="p-2 col-4 btn-secondary"
              onClick={handleAddStyle}
            >
              Add Style
            </Button>
          </Row>
          <div>
            <Button
              onClick={() =>
                download(parseObjectToPSRT(clearSub), "sub.psrt", "jpg")
              }
              variant="primary"
            >
              Dowload Subtitle (.psrt)
            </Button>
            <Button
              onClick={() =>
                download(JSON.stringify(clearSub), "sub.json", "jpg")
              }
              variant="primary"
            >
              Dowload Subtitle (.json)
            </Button>
          </div>
        </div>
      )}
    </Form>
  );
};

function download(data, filename, type) {
  var file = new Blob([data], { type: type });
  if (window.navigator.msSaveOrOpenBlob)
    // IE10+
    window.navigator.msSaveOrOpenBlob(file, filename);
  else {
    // Others
    var a = document.createElement("a"),
      url = URL.createObjectURL(file);
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    setTimeout(function () {
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    }, 0);
  }
}

const getLocalImage = (callback: (fr: FileReader) => any) => (evt) => {
  let tgt = evt.target;
  let files = tgt.files;

  // FileReader support
  if (FileReader && files && files.length) {
    let fr = new FileReader();
    fr.onload = () => callback(fr);
    fr.readAsDataURL(files[0]);
  }
};

const getLocalSubtitle = (callback: (fr: FileReader) => any) => (evt) => {
  let tgt = evt.target;
  let files = tgt.files;

  // FileReader support
  if (FileReader && files && files.length) {
    let fr = new FileReader();
    fr.onload = () => callback(fr);
    fr.readAsText(files[0]);
  }
};

